import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Modal from 'react-modal';
import './BookCard.css';

// StarRating component to display gold stars based on rating
function StarRating({ rating }) {
  const stars = [];
  for (let i = 1; i <= 5; i++) {
    if (i <= Math.floor(rating)) {
      stars.push(<span key={i}>&#9733;</span>); // Full star
    } else {
      stars.push(<span key={i}>&#9734;</span>); // Empty star
    }
  }
  return (
    <div className="star-rating">
      {stars} <span className="rating-value">{rating}</span>
    </div>
  );
}

function BookCard({ book }) {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [formData, setFormData] = useState({
    rating: 0,
    comment: ''
  });

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission here
    console.log(formData);
    // Close the modal after submission
    closeModal();
  };

  return (
    <div className="book-card">
      <img src={book.coverUrl} alt={book.title} onClick={openModal} />
      <div className="details">
        <h2>{book.title}</h2>
        <div className="rating-container">
          <StarRating rating={book.rating} />
        </div>
        <p>Author: {book.author}</p>
        <p>Genre: {book.genre}</p>
      </div>
      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Submit Review"
        className="review-modal"
        overlayClassName="overlay"
      >
       <h2>Submit Review</h2>
        <form onSubmit={handleSubmit} className="modal-form">
          <label>
            Rating:
            <input
              type="number"
              name="rating"
              value={formData.rating}
              onChange={handleChange}
              min="1"
              max="5"
              required
            />
          </label>
          <label>
            Comment:
            <textarea
              name="comment"
              value={formData.comment}
              onChange={handleChange}
              required
            />
          </label>
          <button type="submit">Submit Review</button>
        </form>
        <button onClick={closeModal}>Cancel</button>
      </Modal>
    </div>
  );
}

export default BookCard;
