import React, { useState, useEffect } from 'react';
import BookCard from './BookCard';
import './BookListingPage.css';

function BookListingPage() {
  const [books, setBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [booksPerPage] = useState(8);
  const [sortBy, setSortBy] = useState('title'); // Default sorting by title

  useEffect(() => {
    // Fetch books from backend API
    fetch('http://localhost:5000/api/books')
      .then(response => response.json())
      .then(data => {
        console.log('Data received from API:', data);
        setBooks(data);
        setFilteredBooks(data);
      })
      .catch(error => {
        console.error('Error fetching books:', error);
        console.log(error);
      });
  }, []);

  // Filter books based on search term
  useEffect(() => {
    const filtered = books.filter(book =>
      book.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      book.author.toLowerCase().includes(searchTerm.toLowerCase()) ||
      book.genre.toLowerCase().includes(searchTerm.toLowerCase()) // Assuming genre is a property of the book object
    );
    setFilteredBooks(filtered);
  }, [books, searchTerm]);

  // Pagination
  const indexOfLastBook = currentPage * booksPerPage;
  const indexOfFirstBook = indexOfLastBook - booksPerPage;
  const currentBooks = filteredBooks.slice(indexOfFirstBook, indexOfLastBook);

  // Sorting
  useEffect(() => {
    const sortedBooks = [...filteredBooks].sort((a, b) => {
      if (a[sortBy] < b[sortBy]) return -1;
      if (a[sortBy] > b[sortBy]) return 1;
      return 0;
    });
    setFilteredBooks(sortedBooks);
  }, [filteredBooks, sortBy]);

  // Handle search term change
  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
    setCurrentPage(1); // Reset page when search term changes
  };

  // Handle sorting change
  const handleSortChange = (e) => {
    setSortBy(e.target.value);
  };

  // Pagination controls
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div className="book-listing">
      <h1>Book Listing</h1>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search by title, author, or genre"
          value={searchTerm}
          onChange={handleSearchChange}
        />
      </div>
      <div className="sorting">
        <label htmlFor="sortBy">Sort By:</label>
        <select id="sortBy" value={sortBy} onChange={handleSortChange}>
          <option value="title">Title</option>
          <option value="author">Author</option>
          <option value="rating">Rating</option>
          {/* Add more sorting options if needed */}
        </select>
      </div>
      <div className="books-grid">
        {currentBooks.map(book => (
          <BookCard key={book.id} bookUniqueId={book._id} book={book} />
        ))}
      </div>
      {/* Pagination controls */}
      <div className="pagination">
        {Array.from({ length: Math.ceil(filteredBooks.length / booksPerPage) }).map((_, index) => (
          <button key={index} onClick={() => paginate(index + 1)}>{index + 1}</button>
        ))}
      </div>
    </div>
  );
}

export default BookListingPage;
